INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2015-06-19 09:55:39', '2015-06-19 09:55:39', 'Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'trash', 'open', 'open', '', 'hello-world', '', '', '2015-06-29 12:05:28', '2015-06-29 12:05:28', '', 0, 'http://jordi.local:8888/fhp/idl/?p=1', 1, 'post', '', 1),
(2, 1, '2015-06-19 09:55:39', '2015-06-19 09:55:39', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://jordi.local:8888/fhp/idl/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'trash', 'open', 'open', '', 'sample-page', '', '', '2015-06-22 10:04:29', '2015-06-22 10:04:29', '', 0, 'http://jordi.local:8888/fhp/idl/?page_id=2', 0, 'page', '', 0),
(4, 1, '2015-06-22 10:04:29', '2015-06-22 10:04:29', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://jordi.local:8888/fhp/idl/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'inherit', 'open', 'open', '', '2-revision-v1', '', '', '2015-06-22 10:04:29', '2015-06-22 10:04:29', '', 2, 'http://jordi.local:8888/fhp/idl/2015/06/22/2-revision-v1/', 0, 'revision', '', 0),
(5, 1, '2015-06-22 10:04:56', '2015-06-22 10:04:56', '', 'Projects', 'In the last few years, we were continously working both on academic as well as industry reasearch. Here you find an overview of our projects. ', 'publish', 'open', 'open', '', 'projects', '', '', '2015-06-28 23:28:30', '2015-06-28 23:28:30', '', 0, 'http://jordi.local:8888/fhp/idl/?page_id=5', 3, 'page', '', 0),
(6, 1, '2015-06-22 10:04:56', '2015-06-22 10:04:56', '', 'Projects', '', 'inherit', 'open', 'open', '', '5-revision-v1', '', '', '2015-06-22 10:04:56', '2015-06-22 10:04:56', '', 5, 'http://jordi.local:8888/fhp/idl/2015/06/22/5-revision-v1/', 0, 'revision', '', 0),
(7, 1, '2015-06-22 10:05:12', '2015-06-22 10:05:12', '', 'About', 'The Interaction Design Lab brings together the research interests and expertise in the field of interactive systems and applications from the design area. We see ourselves as partners in business and academia. Our aim is to design, develop and pioneer new forms of interactive systems, objects and spaces. Here, you can find a list of our current research topics.', 'publish', 'open', 'open', '', 'about', '', '', '2015-06-28 23:15:21', '2015-06-28 23:15:21', '', 0, 'http://jordi.local:8888/fhp/idl/?page_id=7', 2, 'page', '', 0),
(8, 1, '2015-06-22 10:05:12', '2015-06-22 10:05:12', '', 'About', '', 'inherit', 'open', 'open', '', '7-revision-v1', '', '', '2015-06-22 10:05:12', '2015-06-22 10:05:12', '', 7, 'http://jordi.local:8888/fhp/idl/2015/06/22/7-revision-v1/', 0, 'revision', '', 0),
(9, 1, '2015-06-22 10:05:24', '2015-06-22 10:05:24', '', 'People', 'We have a very dynamic team structure. Depending on the requirements of the project, we invite a very diverse range of specialists to participate in our work. Furthermore, we usually include a number of students from the Interface Design programme in our projects.', 'publish', 'open', 'open', '', 'people', '', '', '2015-06-28 17:35:45', '2015-06-28 17:35:45', '', 0, 'http://jordi.local:8888/fhp/idl/?page_id=9', 5, 'page', '', 0),
